-- When API key is used:
-- 1. Convert permission set names â†’ UUIDs
-- 2. Check if required permission UUID is in key's permission UUIDs
-- 3. Grant or deny accessPermission Checking MiddlewareCopy// Express middleware example
async function requirePermission(permissionName: string) {
return async (req, res, next) => {
// Get user or API key from request
const principal = req.user || req.apiKey;
if (!principal) {
return res.status(401).json({ error: 'Unauthorized' });
}
// Check permission (in-memory, instant)
const permissionUUID = getPermissionUUID(permissionName);
const hasPermission = principal.permissions.includes(permissionUUID);
if (!hasPermission) {
return res.status(403).json({
error: 'Forbidden',
required_permission: permissionName
});
}
next();
};
}
// Usage
app.post('/api/v1/workflows/:id/execute',
requirePermission('ps_workflows_execute'),
async (req, res) => {
// Execute workflow
}
);Resource CategoriesCategoryExample ResourcesPermission SetsWorkflowsExecute, Create, Deleteps_workflows_*AI AgentsCreate, Manage, Chatps_ai_agents_*DataExport, Import, Bulk Editps_data_*ReportsCreate, Run, Exportps_reports_*PortalsCreate, Configure, Deployps_portals_*FunctionsDeploy, Execute, Monitorps_functions_*StorageUpload, Download, Deleteps_storage_*Rate Limiting by ResourceCopy-- Set rate limits per resource
